    func calculatePuzzle1() -> Int {
        var floor = 0;
        for character in input.characters {
            if character == "(" {
                floor += 1
            }
            else {
                floor -= 1
            }
        }
        return floor
    }
    
    func calculatePuzzle2() -> Int {
        var floor = 0;
        var position = 1;
        for character in input.characters {
            if character == "(" {
                floor += 1
            }
            else {
                floor -= 1
            }
            if floor < 0 {
                return position
            }
            position += 1
        }
        // never going to get here (I assume)
        return position
    }

